export default function aStarAnimated() {
  let openSet = [startNode];
  let cameFrom = {};
  let gScore = {};
  let fScore = {};
  gScore[startNode] = 0;
  fScore[startNode] = heuristic(startNode, endNode);

  function step() {
    if (openSet.length === 0) return;

    openSet.sort((a, b) => fScore[a] - fScore[b]);
    let currentNode = openSet.shift();

    if (currentNode[0] === endNode[0] && currentNode[1] === endNode[1]) {
      reconstructPath(cameFrom, currentNode);
      return;
    }

    getNeighbors(currentNode).forEach((neighbor) => {
      let tentativeGScore = gScore[currentNode] + 1;

      if (!gScore[neighbor] || tentativeGScore < gScore[neighbor]) {
        cameFrom[neighbor] = currentNode;
        gScore[neighbor] = tentativeGScore;
        fScore[neighbor] = tentativeGScore + heuristic(neighbor, endNode);
        if (!openSet.includes(neighbor)) openSet.push(neighbor);
      }
    });

    nodes[currentNode[0]][currentNode[1]].classList.add("visited");

    setTimeout(step, 50); // Delay for animation
  }

  step();
}

document.getElementById("startButton").addEventListener("click", () => {
  aStarAnimated();
});
