import React, { useState, useEffect } from "react";
import "./App.css";
import Dijkstras from "./Dijkstras";
import AStar from "./AStar";

function App() {
  const rows = 20;
  const cols = 20;
  const [startNode, setStartNode] = useState([9, 9]);
  const [endNode, setEndNode] = useState([0, 0]);
  const [draggingNodeType, setDraggingNodeType] = useState(null);
  const [algorithmToRun, setAlgorithmToRun] = useState(null);
  const [grid, setGrid] = useState(createGrid());

  // Function to create a grid with start and end nodes
  function createGrid() {
    const grid = [];
    for (let row = 0; row < rows; row++) {
      const currentRow = [];
      for (let col = 0; col < cols; col++) {
        currentRow.push({
          row,
          col,
          isStart: row === startNode[0] && col === startNode[1],
          isEnd: row === endNode[0] && col === endNode[1],
          isVisited: false,
          isPath: false,
        });
      }
      grid.push(currentRow);
    }
    return grid;
  }

  // Re-create grid whenever the start or end node changes
  useEffect(() => {
    const newGrid = createGrid();
    setGrid(newGrid);

    // Run the selected algorithm when start or end node is placed down
    if (algorithmToRun && draggingNodeType === null) {
      if (algorithmToRun === "dijkstra") {
        Dijkstras(newGrid, startNode, endNode, setGrid);
      } else if (algorithmToRun === "astar") {
        AStar(newGrid, startNode, endNode, setGrid);
      }
    }
  }, [startNode, endNode, draggingNodeType]);

  const startDijkstras = () => {
    setAlgorithmToRun("dijkstra");
    resetBoard();
  };

  const startAStar = () => {
    setAlgorithmToRun("astar");
    resetBoard();
  };

  const resetBoard = () => {
    setGrid(createGrid());
  };

  const handleMouseDown = (nodeType) => {
    setDraggingNodeType(nodeType);
  };

  const handleMouseUp = () => {
    setDraggingNodeType(null);
  };

  const handleMouseEnter = (row, col) => {
    if (draggingNodeType === "start") {
      setStartNode([row, col]);
    } else if (draggingNodeType === "end") {
      setEndNode([row, col]);
    }
  };

  return (
    <div className="App">
      <h1>Pathfinding Algorithm Visualizer</h1>
      <div className="buttons">
        <button onClick={startDijkstras}>Start Dijkstra's Algorithm</button>
        <button onClick={startAStar}>Start A Star Algorithm</button>
        <button onClick={resetBoard}>Reset Board</button>
      </div>
      <div className="grid" onMouseUp={handleMouseUp}>
        {grid.map((row, rowIdx) => (
          <div key={rowIdx} className="grid-row">
            {row.map((node, nodeIdx) => (
              <div
                key={nodeIdx}
                className={`node 
                  ${node.isStart ? "start" : ""} 
                  ${node.isEnd ? "end" : ""} 
                  ${
                    !node.isStart && !node.isEnd && node.isVisited
                      ? "visited"
                      : ""
                  } 
                  ${node.isPath ? "path" : ""}`}
                onMouseDown={() =>
                  node.isStart
                    ? handleMouseDown("start")
                    : node.isEnd
                    ? handleMouseDown("end")
                    : null
                }
                onMouseEnter={() => handleMouseEnter(node.row, node.col)}
              ></div>
            ))}
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
