import React, { useState } from "react";
import "./App.css";
import dijkstra from "./dijkstra";

function App() {
  const rows = 20;
  const cols = 20;
  const START_NODE = [0, 0];
  const END_NODE = [rows - 1, cols - 1];

  const createGrid = () => {
    const grid = [];
    for (let row = 0; row < rows; row++) {
      const currentRow = [];
      for (let col = 0; col < cols; col++) {
        currentRow.push({
          row,
          col,
          isStart: row === START_NODE[0] && col === START_NODE[1],
          isEnd: row === END_NODE[0] && col === END_NODE[1],
          isVisited: false,
          isPath: false,
        });
      }
      grid.push(currentRow);
    }
    return grid;
  };

  const [grid, setGrid] = useState(createGrid());

  const startAlgorithm = () => {
    dijkstra(grid, START_NODE, END_NODE, setGrid);
  };

  return (
    <div className="App">
      <h1>Pathfinding Algorithm Visualizer</h1>
      <button onClick={startAlgorithm}>Start Dijkstra's Algorithm</button>
      <div className="grid">
        {grid.map((row, rowIdx) => (
          <div key={rowIdx} className="grid-row">
            {row.map((node, nodeIdx) => (
              <div
                key={nodeIdx}
                className={`node 
                  ${node.isStart ? "start" : ""} 
                  ${node.isEnd ? "end" : ""} 
                  ${node.isVisited ? "visited" : ""} 
                  ${node.isPath ? "path" : ""}`}
              ></div>
            ))}
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
