import React, { useState } from "react";
import "./App.css";
import Dijkstras from "./Dijkstras";
import AStar from "./AStar";

function App() {
  const rows = 20;
  const cols = 20;
  const START_NODE = [9, 9];
  const END_NODE = [0, 0];

  const createGrid = () => {
    const grid = [];
    for (let row = 0; row < rows; row++) {
      const currentRow = [];
      for (let col = 0; col < cols; col++) {
        currentRow.push({
          row,
          col,
          isStart: row === START_NODE[0] && col === START_NODE[1],
          isEnd: row === END_NODE[0] && col === END_NODE[1],
          isVisited: false,
          isPath: false,
        });
      }
      grid.push(currentRow);
    }
    return grid;
  };

  const [grid, setGrid] = useState(createGrid());

  const startDijkstras = () => {
    resetBoard();
    setTimeout(() => {
      Dijkstras(grid, START_NODE, END_NODE, setGrid);
    }, 10); // Delay by 0ms to ensure reset takes place first
  };

  const startAStar = () => {
    resetBoard();
    setTimeout(() => {
      AStar(grid, START_NODE, END_NODE, setGrid);
    }, 10); // Delay by 0ms to ensure reset takes place first
  };

  const resetBoard = () => {
    setGrid(createGrid());
  };

  return (
    <div className="App">
      <h1>Pathfinding Algorithm Visualizer</h1>
      <div className="buttons">
        <button onClick={startDijkstras}>Start Dijkstra's Algorithm</button>
        <button onClick={startAStar}>Start A Star Algorithm</button>
        <button onClick={resetBoard}>Reset Board</button>
      </div>
      <div className="grid">
        {grid.map((row, rowIdx) => (
          <div key={rowIdx} className="grid-row">
            {row.map((node, nodeIdx) => (
              <div
                key={nodeIdx}
                className={`node 
                  ${node.isStart ? "start" : ""} 
                  ${node.isEnd ? "end" : ""} 
                  ${
                    !node.isStart && !node.isEnd && node.isVisited
                      ? "visited"
                      : ""
                  } 
                  ${node.isPath ? "path" : ""}`}
              ></div>
            ))}
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
