const getNeighbors = (node, rows, cols) => {
  const [row, col] = node;
  const neighbors = [];

  // Move up
  if (row > 0) neighbors.push([row - 1, col]);

  // Move down
  if (row < rows - 1) neighbors.push([row + 1, col]);

  // Move left
  if (col > 0) neighbors.push([row, col - 1]);

  // Move right
  if (col < cols - 1) neighbors.push([row, col + 1]);
  return neighbors;
};

const Dijkstras = (grid, startNode, endNode, setGrid) => {
  const rows = grid.length;
  const cols = grid[0].length;

  let openSet = [startNode];
  let cameFrom = {};
  let gScore = {};

  // Initialize scores
  for (let row = 0; row < rows; row++) {
    for (let col = 0; col < cols; col++) {
      gScore[[row, col]] = Infinity;
    }
  }

  gScore[startNode] = 0;

  function step() {
    if (openSet.length === 0) return;

    // Sort by lowest fScore
    openSet.sort((a, b) => fScore[a] - fScore[b]);
    let currentNode = openSet.shift();

    // Visualize the current node
    const newGrid = grid.slice();
    newGrid[currentNode[0]][currentNode[1]].isVisited = true;
    setGrid(newGrid);

    if (currentNode[0] === endNode[0] && currentNode[1] === endNode[1]) {
      reconstructPath(cameFrom, currentNode);
      return;
    }

    getNeighbors(currentNode, rows, cols).forEach((neighbor) => {
      let tentativeGScore = gScore[currentNode] + 1;

      if (tentativeGScore < gScore[neighbor]) {
        cameFrom[neighbor] = currentNode;
        gScore[neighbor] = tentativeGScore;
        fScore[neighbor] = tentativeGScore + heuristic(neighbor, endNode);
        if (!openSet.includes(neighbor)) openSet.push(neighbor);
      }
    });

    setTimeout(step, 50); // Animation delay
  }

  function reconstructPath(cameFrom, currentNode) {
    const newGrid = grid.slice();
    while (cameFrom[currentNode]) {
      currentNode = cameFrom[currentNode];
      newGrid[currentNode[0]][currentNode[1]].isPath = true;
    }
    setGrid(newGrid);
  }

  step();
};

export default Dijkstras;
