const getNeighbors = (node, rows, cols) => {
  const [row, col] = node;
  const neighbors = [];
  if (row > 0) neighbors.push([row - 1, col]);
  if (row < rows - 1) neighbors.push([row + 1, col]);
  if (col > 0) neighbors.push([row, col - 1]);
  if (col < cols - 1) neighbors.push([row, col + 1]);
  return neighbors;
};

// Heuristic function: Manhattan distance
const heuristic = (node, endNode) => {
  const [row, col] = node;
  const [endRow, endCol] = endNode;
  return Math.abs(row - endRow) + Math.abs(col - endCol);
};

// Check if two nodes are equal
const isEqual = (node1, node2) => {
  return node1[0] === node2[0] && node1[1] === node2[1];
};

const AStar = (grid, startNode, endNode, setGrid, onComplete) => {
  const rows = grid.length;
  const cols = grid[0].length;

  let openSet = [startNode];
  let cameFrom = {};
  let gScore = {};
  let fScore = {};

  // Initialize scores
  for (let row = 0; row < rows; row++) {
    for (let col = 0; col < cols; col++) {
      gScore[[row, col]] = Infinity;
      fScore[[row, col]] = Infinity;
    }
  }
  gScore[startNode] = 0;
  fScore[startNode] = heuristic(startNode, endNode);

  function step() {
    if (openSet.length === 0) {
      // If there are no more nodes to explore, reconstruct the path
      reconstructPath(cameFrom, startNode, endNode);
      if (onComplete) onComplete(); // Signal completion
      return;
    }

    // Find the node with the lowest fScore in the open set
    let currentNode = openSet.reduce((bestNode, node) => {
      return fScore[node] < fScore[bestNode] ? node : bestNode;
    });

    if (isEqual(currentNode, endNode)) {
      reconstructPath(cameFrom, startNode, endNode);
      if (onComplete) onComplete(); // Signal completion
      return;
    }

    openSet = openSet.filter((node) => !isEqual(node, currentNode));

    // Update the grid with the current node as visited
    const newGrid = grid.map((row) =>
      row.map((node) => ({
        ...node,
        isVisited:
          node.row === currentNode[0] && node.col === currentNode[1]
            ? true
            : node.isVisited,
      }))
    );
    setGrid(newGrid);

    // Explore neighbors
    getNeighbors(currentNode, rows, cols).forEach((neighbor) => {
      let tentativeGScore = gScore[currentNode] + 1;

      if (tentativeGScore < gScore[neighbor]) {
        cameFrom[neighbor] = currentNode;
        gScore[neighbor] = tentativeGScore;
        fScore[neighbor] = gScore[neighbor] + heuristic(neighbor, endNode);

        if (!openSet.some((n) => isEqual(n, neighbor))) {
          openSet.push(neighbor);
        }
      }
    });

    setTimeout(step, 20); // Animation delay
  }

  function reconstructPath(cameFrom, startNode, endNode) {
    let currentNode = endNode;
    const newGrid = grid.map((row) =>
      row.map((node) => ({
        ...node,
        isPath: false,
      }))
    );
    while (currentNode && !isEqual(currentNode, startNode)) {
      if (!isEqual(currentNode, endNode)) {
        newGrid[currentNode[0]][currentNode[1]].isPath = true;
      }
      currentNode = cameFrom[currentNode];
    }
    setGrid(newGrid);
  }

  step();
};

export default AStar;
