const getNeighbors = (node, rows, cols) => {
  const [row, col] = node;
  const neighbors = [];
  if (row > 0) neighbors.push([row - 1, col]);
  if (row < rows - 1) neighbors.push([row + 1, col]);
  if (col > 0) neighbors.push([row, col - 1]);
  if (col < cols - 1) neighbors.push([row, col + 1]);
  return neighbors;
};

const Dijkstras = (grid, startNode, endNode, setGrid, onComplete) => {
  const rows = grid.length;
  const cols = grid[0].length;

  let openSet = [startNode];
  let cameFrom = {};
  let gScore = {};
  let metrics = {
    pathLength: 0,
    nodesVisited: 0,
    runtime: 0,
  };

  // Initialize scores
  for (let row = 0; row < rows; row++) {
    for (let col = 0; col < cols; col++) {
      gScore[[row, col]] = Infinity;
    }
  }
  gScore[startNode] = 0;

  const startTime = Date.now();

  function step() {
    if (openSet.length === 0) {
      // If there are no more nodes to explore, reconstruct the path
      metrics.pathLength = reconstructPath(cameFrom, startNode, endNode, grid);
      metrics.runtime = (Date.now() - startTime) / 1000; // runtime in seconds
      if (onComplete) onComplete(grid, metrics); // Signal completion
      return;
    }

    // Visualize the current node
    let currentNode = openSet.shift();

    if (currentNode.toString() === endNode.toString()) {
      metrics.pathLength = reconstructPath(cameFrom, startNode, endNode, grid);
      metrics.runtime = (Date.now() - startTime) / 1000; // runtime in seconds
      if (onComplete) onComplete(grid, metrics); // Signal completion
      return;
    }

    // Create a new grid copy
    const newGrid = grid.map((row) =>
      row.map((node) => ({
        ...node,
        isVisited: node.isVisited || false, // Ensure existing visited status is retained
        isPath: node.isPath || false, // Ensure existing path status is retained
      }))
    );

    newGrid[currentNode[0]][currentNode[1]].isVisited = true;
    metrics.nodesVisited++;
    setGrid(newGrid); // Update the grid with visited nodes

    // Spread to neighbors
    getNeighbors(currentNode, rows, cols).forEach((neighbor) => {
      let tentativeGScore = gScore[currentNode] + 1;

      if (tentativeGScore < gScore[neighbor]) {
        cameFrom[neighbor] = currentNode;
        gScore[neighbor] = tentativeGScore;
        if (!openSet.some((n) => n[0] === neighbor[0] && n[1] === neighbor[1]))
          openSet.push(neighbor);
      }
    });

    setTimeout(step, 20); // Animation delay
  }

  function reconstructPath(cameFrom, startNode, endNode, grid) {
    let currentNode = endNode;
    const newGrid = grid.map((row) =>
      row.map((node) => ({
        ...node,
        isPath: false, // Reset all path nodes to false initially
      }))
    );
    let pathLength = 0;
    while (currentNode && currentNode.toString() !== startNode.toString()) {
      if (currentNode !== endNode) {
        newGrid[currentNode[0]][currentNode[1]].isPath = true;
        pathLength++;
      }
      currentNode = cameFrom[currentNode];
    }
    // Ensure the final path is updated and visible
    setGrid(newGrid);
    return pathLength;
  }

  step();
};

export default Dijkstras;
