import React, { useState, useEffect } from "react";
import "./App.css";
import Dijkstras from "./Dijkstras";
import AStar from "./AStar";

function App() {
  const rows = 20;
  const cols = 20;
  const [startNode, setStartNode] = useState([9, 9]);
  const [endNode, setEndNode] = useState([0, 0]);
  const [draggingNodeType, setDraggingNodeType] = useState(null);
  const [grid, setGrid] = useState(createGrid());
  const [algorithmToRun, setAlgorithmToRun] = useState(null);
  const [running, setRunning] = useState(false);

  // Create a new grid based on the start and end node positions
  function createGrid() {
    const grid = [];
    for (let row = 0; row < rows; row++) {
      const currentRow = [];
      for (let col = 0; col < cols; col++) {
        currentRow.push({
          row,
          col,
          isStart: row === startNode[0] && col === startNode[1],
          isEnd: row === endNode[0] && col === endNode[1],
          isVisited: false,
          isPath: false,
        });
      }
      grid.push(currentRow);
    }
    return grid;
  }

  const resetBoard = () => {
    if (!running) {
      setGrid(createGrid());
    }
  };

  const startAlgorithm = (algorithm) => {
    if (!running) {
      resetBoard();
      setAlgorithmToRun(algorithm);
    }
  };

  // Run the selected algorithm
  useEffect(() => {
    if (algorithmToRun && !running) {
      setRunning(true);
      const newGrid = createGrid();
      setGrid(newGrid);

      if (algorithmToRun === "dijkstra") {
        Dijkstras(
          newGrid,
          startNode,
          endNode,
          setGrid,
          () => (setRunning(false), setAlgorithmToRun(null))
        );
      } else if (algorithmToRun === "astar") {
        AStar(
          newGrid,
          startNode,
          endNode,
          setGrid,
          () => (setRunning(false), setAlgorithmToRun(null))
        );
      }

      setAlgorithmToRun(null);
    }
  }, [algorithmToRun]);

  const handleMouseDown = (nodeType) => {
    setDraggingNodeType(nodeType);
  };

  const handleMouseUp = () => {
    setDraggingNodeType(null);
  };

  const handleMouseEnter = (row, col) => {
    if (draggingNodeType === "start") {
      setStartNode([row, col]);
    } else if (draggingNodeType === "end") {
      setEndNode([row, col]);
    }
  };

  useEffect(() => {
    // Update the grid whenever the start or end node changes
    setGrid(createGrid());
  }, [startNode, endNode]);

  return (
    <div className="App">
      <h1>Pathfinding Algorithm Visualizer</h1>
      <div className="buttons">
        <select
          onChange={(e) => startAlgorithm(e.target.value)}
          value={algorithmToRun || ""}
          disabled={running}
        >
          <option value="" disabled>
            Select Algorithm
          </option>
          <option value="dijkstra">Dijkstra's Algorithm</option>
          <option value="astar">A* Algorithm</option>
        </select>
        <button onClick={resetBoard} disabled={running}>
          Reset Board
        </button>
      </div>
      <div className="grid-container">
        {" "}
        {/* Wrapper for the grid */}
        <div className="grid" onMouseUp={handleMouseUp}>
          {grid.map((row, rowIdx) => (
            <div key={rowIdx} className="grid-row">
              {row.map((node, nodeIdx) => (
                <div
                  key={nodeIdx}
                  className={`node 
                    ${node.isStart ? "start" : ""} 
                    ${node.isEnd ? "end" : ""} 
                    ${
                      !node.isStart && !node.isEnd && node.isVisited
                        ? "visited"
                        : ""
                    } 
                    ${node.isPath ? "path" : ""}`}
                  onMouseDown={() =>
                    node.isStart
                      ? handleMouseDown("start")
                      : node.isEnd
                      ? handleMouseDown("end")
                      : null
                  }
                  onMouseEnter={() => handleMouseEnter(node.row, node.col)}
                ></div>
              ))}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default App;
